const menu=document.querySelector(".icon-menu"),navBar=document.querySelector(".header__nav-bar"),openMenu=a=>{a?(navBar.classList.toggle("header__nav-bar_active"),menu.classList.toggle("icon-menu_active")):(navBar.classList.remove("header__nav-bar_active"),menu.classList.remove("icon-menu_active"))};menu.addEventListener("click",el=>{el.preventDefault(),openMenu(!0)});